{
    options = {loops: true};
    {
        while (foo()) bar();
    }
    {
        for (; foo();) bar();
    }
}

{
    options = {loops: true};
    {
        for (; ;)
            if (foo()) break;
    }
    {
        for (; !foo(););
    }
}

{
    options = {loops: true};
    {
        for (; bar();)
            if (foo()) break;
    }
    {
        for (; bar() && !foo(););
    }
}

{
    options = {loops: true};
    {
        for (; bar();) {
            if (foo()) break;
            stuff1();
            stuff2();
        }
    }
    {
        for (; bar() && !foo();) {
            stuff1();
            stuff2();
        }
    }
}

{
    options = {loops: true, sequences: true};
    {
        for (; bar();) {
            x();
            y();
            if (foo()) break;
            z();
            k();
        }
    }
    {
        for (; bar() && (x(), y(), !foo());) z(), k();
    }
}

{
    options = {loops: true};
    {
        for (; ;) if (foo()) bar(); else break;
    }
    {
        for (; foo();) bar();
    }
}

{
    options = {loops: true};
    {
        for (; bar();) {
            if (foo()) baz();
            else break;
        }
    }
    {
        for (; bar() && foo();) baz();
    }
}

{
    options = {loops: true};
    {
        for (; bar();) {
            if (foo()) baz();
            else break;
            stuff1();
            stuff2();
        }
    }
    {
        for (; bar() && foo();) {
            baz();
            stuff1();
            stuff2();
        }
    }
}

{
    options = {loops: true, sequences: true};
    {
        for (; bar();) {
            x();
            y();
            if (foo()) baz();
            else break;
            z();
            k();
        }
    }
    {
        for (; bar() && (x(), y(), foo());) baz(), z(), k();
    }
}
